    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        movimientos = 0;
        this.pantallaMovimientos.setText("");
        ResolucionAutomatica(Integer.valueOf(TorreHanoi.config.discos), 1, 2, 3);
        this.pantallaMovimientos.setText(this.pantallaMovimientos.getText() + "Se termin칩 el juego con " + movimientos + " movimientos");
    }//GEN-LAST:event_jButton1ActionPerformed
    
    public void ResolucionAutomatica (int discos, int torre1, int torre2, int torre3){
        if (discos == 1){
            this.pantallaMovimientos.setText(this.pantallaMovimientos.getText() + "Se movi칩 el disco #" + discos + " de la torre #" + torre1 + " a la torre #" + torre3 + "\n\n");
            movimientos ++;
            this.labelMovimientos.setText(String.valueOf(movimientos));
        } else {
            ResolucionAutomatica(discos - 1, torre1, torre3, torre2);
            this.pantallaMovimientos.setText(this.pantallaMovimientos.getText() + "Se movi칩 el disco #" + discos + " de la torre #" + torre1 + " a la torre #" + torre3 + "\n\n");
            movimientos ++;
            this.labelMovimientos.setText(String.valueOf(movimientos));
            ResolucionAutomatica(discos -1, torre2, torre1, torre3);
        }
    }
    
